using NUnit.Framework;
using System;
namespace StudyIt.Tests;

public class FileConversionTests
{
    // Arrange
    string base64JpgResult;
    string base64Jpg;
    string base64String;
    string base64StringPng;
    string base64ToBinAndBack;

    [SetUp]
    public void Setup()
    {
        // Act
        base64Jpg = "data:image/jpg;base64,";
        base64String = "";
        base64StringPng = "data:image/png;base64,";
        base64JpgResult = FileConversion.AddFileExtension(base64String);
        base64ToBinAndBack = FileConversion.BinToBase64String(FileConversion.Base64StringtoBin(base64String));

    }
    //Assert
    [Test]
    public void fileExtensionEqualPass()
    {
        Assert.AreEqual(base64JpgResult, base64Jpg);
    }
    [Test]
    public void fileExtensionEqualFails()
    {
        Assert.AreNotEqual(base64JpgResult, base64StringPng);
    }
    [Test]
    public void Base64ToBinAndBackPass()
    {
        Assert.AreEqual(base64String, base64ToBinAndBack);
    }
}